import React, { useEffect, useState } from "react";
import { Card, Input, Select, Button, Form } from "antd";
import { CheckCircleTwoTone } from "@ant-design/icons";
import { reducer, fetchUsers } from "../others/asyncAction";
import store from "../redux/store";
const UserDetail = () => {
  const [user, setUser] = useState({ firstname: "", email: "", isTrue: false });
  store.subscribe(() => {
    if (store.getState().isTrue) {
      setUser({ isTrue: true });
    }
  });
  const handleSubmit = (event) => {
    // setUser({isTrue : true});
    store.dispatch(fetchUsers());
    localStorage.setItem("user", user.firstname);
  };
  const onChange = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log(user);
  };

  return (
    <div>
      <Card
        hoverable
        borderd={true}
        style={{backgroundImage: "linear-gradient(to right,sea,skyblue)",

          width: 600,
          height: 600,
          border: 1,
         // borderColor: "red",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          margin:"auto"
          // marginLeft: 700,

        }}
      >
        <Form onFinish={handleSubmit}>
          <h2>Registration Form</h2>
          <Form.Item label="FirstName">
            <Input
              name="firstname"
              onChange={onChange}
              style={{ width: 400 }}
            />
          </Form.Item>
          <Form.Item value="lastname" label="LastName">
            <Input name="lastname" />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input name="email" onChange={onChange} />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input name="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <CheckCircleTwoTone
              style={{ fontSize: "15px" }}
              twoToneColor={user.isTrue ? "black" : "red"}
            ></CheckCircleTwoTone>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;

