import React, { useEffect, useState } from "react";
import { Card, Input, Select, Button, Form } from "antd";
import { CheckCircleTwoTone } from "@ant-design/icons";
const redux = require("redux");
const createStore = redux.createStore;
const axios = require("axios");


const reducer = (state = initialState, action) => {
        switch (action.type) {
          case FETCH_USER_REQUEST:
            return {
              ...state,
              loading: true,
            };
          case FETCH_USER_SUCCESS:
            return {
              loading: false,
              user: action.payload,
              error: "",
            };
          case FETCH_USER_FAILURE:
            return {
              loading: false,
              user: [],
              error: action.payload,
            };
        }
      };
    const fetchUsers = () => {
        return function (dispatch) {
          dispatch(fetchUsersRequest());
          axios
            .get("https://dummyjson.com/users/1")
            .then((response) => {
              if(response==='OK')
              var users = response.data
              dispatch(fetchUsersSuccess(users));
            })
            .catch((error) => {
              dispatch(fetchUsersFailure(error.message));
            });
        };
      };
    // const store = createStore(reducer);
    // store.subscribe(() => {
    //   console.log(store.getState());
    // });
    // store.dispatch(fetchUsers());
const UserDetail = () => {

  const [user, setUser] = useState({ firstname: "", email: "", isTrue: false });
  const handleSubmit = (event) => {
    //setUser({ isTrue: true });

    dispatch(fetchUsers())
    localStorage.setItem("user", user.firstname);
    localStorage.setItem("email", user.email); 
  }
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
          boxShadow: 10,
        }}
      >
        <Form onFinish={handleSubmit}>
          <Form.Item label="FirstName">
            <Input
              name="firstname"
              onChange={onChange}
              style={{ width: 400 }}
            />
          </Form.Item>
          <Form.Item value="lastname" label="LastName">
            <Input name="lastname" />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input name="email" onChange={onChange} />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input name="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <CheckCircleTwoTone
              style={{ fontSize: "15px" }}
              twoToneColor={user.isTrue ? "black" : "red"}
            ></CheckCircleTwoTone>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
}         
export default UserDetail;

