import React, { useState } from "react";
import { Card, Input, Select, Radio, Button, Form } from "antd";

const store = () => {




  var firstname = document.getElementById("firstname").value;
  //   var lastname = document.getElementById("lastname").value;
  //   var email = document.getElementById("email").value;
  //   var city = document.getElementById("city").value;
  //   var country = document.getElementById("country").value;
  const user = {
    firstname: firstname,
    // lastname: lastname,
    // email: email,
    // city: city,
    // country: country,
  };
  localStorage.setItem("firstname:", user.firstname);
  //window.localStorage.setItem(user.key, JSON.stringify(user));
};
const UserDetail = () => {
  const [firstname, setFirstname] = useState("");
  // const [lastname,setLastname]=useState("");
  // const [email,setEmail]=useState("");
  // const [city,setCity]=useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    // setFirstname()
    // const firstName = event.target.firstname.value
    console.log("firstname>>>", event.target.firstname.value);
    store();

    // const email = event.target.email.value
    // console.log(firstName)
  };
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
        }}
      >
        <Form onsubmit="handleSubmit">
          <Form.Item label="FirstName">
            <Input id="firstname" style={{ width: 400 }} />
          </Form.Item>
          <Form.Item value="lastname" label="LastName">
            <Input id="lastname" />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input id="email" />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input id="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              id="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <Radio id="isactive" />
          </Form.Item>
          <Button type="primary" onClick={handleSubmit}>
            Submit
          </Button>
        </Form>
      </Card>
    </div>
  );
};

export default UserDetail;

