import React, { useState } from "react";
import { Card, Input, Select, Radio, Button, Form } from "antd";
const UserDetail = () => {
 const [firstname, setFirstName] = useState("");
 const [isActive,setisActive]=useState("");
  const handleSubmit = (event) => {
   event.preventDefault();
   localStorage.setItem('firstName', firstname);
   setisActive(true);
  }
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
        }}
      >
        <Form onSubmit={handleSubmit} >
          <Form.Item label="FirstName">
            <Input id="firstname" onChange={(event) => setFirstName(event.target.value)} style={{ width: 400 }} />
          </Form.Item>
          <Form.Item value="lastname" label="LastName">
            <Input id="lastname" />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input id="email" />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input id="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              id="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <Radio id="isactive" />
          </Form.Item>
          <Button type="primary"  >
            Submit
          </Button>
        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;








