import React, { useState } from "react";
import { Card, Input, Select, Button, Form } from "antd";
import { CheckCircleTwoTone } from "@ant-design/icons";
const UserDetail = () => {
  const [user, setUser] = useState({ firstname: "", email: "", isTrue: false });
  const handleSubmit = (event) => {
    setUser((user.isTrue = true));
    // event.preventDefault();
    console.log(">>>>>>>>>>>>..", user.isTrue);
    localStorage.setItem("user", user.firstname);
  };
  const onChange = (event) => {
    console.log(">>>>>>>>>>>>..", user.isTrue);
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log(user);
  };
  use
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
          boxShadow: 5,
        }}
      >
        <Form onFinish={handleSubmit}>
          <Form.Item label="FirstName">
            <Input
              name="firstname"
              onChange={onChange}
              style={{ width: 400 }}
            />
          </Form.Item>
          <Form.Item value="lastname" label="LastName">
            <Input name="lastname" />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input name="email" onChange={onChange} />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input name="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
          <CheckCircleTwoTone 
              style={{ fontSize: "15px" }}
              twoToneColor={user.isTrue ? "black" : "red"}
            ></CheckCircleTwoTone >
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;

