import { BUY_ICECREAM } from "./iceCreamTypes";
const initialState={
    numOfCakes:10
}
const iceCreamReducer = (initialState = state, action) => {
  switch (action.type) {
    case BUY_CAKE:
      return {
        ...state,
        numofIceCreams: state.numofIceCreams - 1,
      };
    default:
      return state;
  }
};
export default iceCreamReducer;
