import React, { useEffect } from "react";
import { Card, Input, Select, Radio, Button, Form } from "antd";

// const store = () => {
//   var firstname = document.getElementById("firstname").value;
//   var lastname = document.getElementById("lastname").value;
//   var email = document.getElementById("email").value;
//   var city = document.getElementById("city").value;
//   var country = document.getElementById("country").value;
//   const user = {
//     firstname: firstname,
//     lastname: lastname,
//     email: email,
//     city: city,
//     country: country,
//   };
//   window.localStorage.setItem(user.key, JSON.stringify(user));
// };

const UserDetail = () => {
    const handleSubmit = (event) => {
        const[firstname,setFirstname]=useState("");
        setFirstname(event.target.value)
        console.log("details>>", firstname);
       };
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
        }}
      >
        <Form>
          <Form.Item value='firstname' label="FirstName" id="firstname">
            <Input style={{ width: 400 }} />
          </Form.Item>
          <Form.Item value='lastname'label="LastName" id="lastname">
            <Input />
          </Form.Item>
          <Form.Item  value='email'label="Email" id="email">
            <Input />
          </Form.Item>
          <Form.Item value='city' label="City" id="city">
            <Input />
          </Form.Item>
          <Form.Item value='country' label="Country" id="country">
            <Select
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <Radio />
          </Form.Item>
          <Button type="primary" onClick={handleSubmit} >
            Submit
          </Button>
        </Form>
      </Card>
    </div>
  );
};

export default UserDetail;

