const UserDetail = () => {
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
        }}
      >
        <Form method="POST" on>
          <Form.Item label="FirstName" id="firstname">
            <Input style={{ width: 400 }} />
          </Form.Item>
          <Form.Item label="LastName" id="lastname">
            <Input />
          </Form.Item>
          <Form.Item label="Email" id="email">
            <Input />
          </Form.Item>
          <Form.Item label="City" id="city">
            <Input />
          </Form.Item>
          <Form.Item label="Country" id="country">
            <Select
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <Radio />
          </Form.Item>
          <Button type="primary"  onClick={handleSubmit}>
            Submit
          </Button>
        </Form>
      </Card>
    </div>
  );
};

export default UserDetail;

