import React, { useEffect, useState } from "react";
import { Card, Input, Select, Button, Form } from "antd";
import { CheckCircleTwoTone } from "@ant-design/icons";
import { reducer, fetchUsers } from "../others/asyncAction";
import store from "../redux/store";
import { AppInput } from "./UserDetail.style";
const UserDetail = () => {
  const [user, setUser] = useState({ firstname: "", email: "", isTrue: false });
  const [selectedOption,setSelectedOption]=useState('');
  const handleDropdownChange=(event)=>{
    console.log(event.targ)
    const selectedValue=event.target.value;
    setSelectedOption(selectedValue);
  }
  store.subscribe(() => {
    if (store.getState().isTrue) {
      setUser({ isTrue: true });
    }
  });
  const handleSubmit = (event) => {
    // setUser({isTrue : true});
    store.dispatch(fetchUsers());
    localStorage.setItem("user", user.firstname);
    // onFinish();
  };
  const onChange = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log(user);
  };
  // const onFinish = (values) => {
  //   console.log("success", values);
  // };
  // const onFinishFailed = (error) => {
  //   console.log("Failed", error);
  // };

  //const
  return (
    <div style={{ width: "100%" }}>
      <Card
        hoverable
        borderd={true}
        style={{
          backgroundImage: "linear-gradient(to right,pink,skyblue)",
          marginTop: 100,
          width: "40%",
          height: 700,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: 100,
          marginLeft: 600,
        }}
      >
        <Form style={{ width: 500 }}>
          <h2>Registration Form</h2>
          <Form.Item
            label="First-Name"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <AppInput
              placeholder="FirstName"
              name="firstname"
              onChange={onChange}
            />
          </Form.Item>
          <Form.Item
            label="Last-Name"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <AppInput placeholder="LastName" name="lastname" />
          </Form.Item>
          <Form.Item
            label="Email"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <AppInput placeholder="Email" name="email" onChange={onChange} />
          </Form.Item>
          <Form.Item
            label="City"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <AppInput placeholder="City" name="city" />
          </Form.Item>
          <Form.Item
            label="Country"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <Select
              value={selectedValue} onChange={handleDropdownChange}
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item
            label="IsActive"
            labelCol={{ span: 4 }}
            wrapperCol={{ span: 20 }}
          >
            <CheckCircleTwoTone
              style={{ fontSize: "15px" }}
              twoToneColor={user.isTrue ? "green" : "red"}
            ></CheckCircleTwoTone>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 4, span: 20 }}>
            <Button type="primary" onClick={handleSubmit}>
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;

