import React, { useState } from "react";
import { Card, Input, Select, Radio, Button, Form } from "antd";
const UserDetail = () => {

const [user,setUser]= useState({firstname:"",email:"",isActive:false})
  const handleSubmit = (event) => {
    setUser(user.isActive=true)
    // event.preventDefault();
    console
   localStorage.setItem('user', user.firstname);
  };
  const onChange=(event)=>{
    setUser({...user,[event.target.name]:event.target.value})
    console.log(user)
  }
  return (
    <div>
      <Card
        borderd={true}
        style={{
          width: 600,
          height: 700,
          borderColor: "gray",
          justifyContent: "center",
          alignContent: "center",
          display: "flex",
          marginLeft: 700,
        }}
      >
        <Form onFinish={handleSubmit} >
          <Form.Item label="FirstName">
            <Input name="firstname" onChange={onChange} style={{ wnameth: 400 }} />
          </Form.Item>
          <Form.Item value="lastname"   label="LastName">
            <Input name="lastname"   />
          </Form.Item>
          <Form.Item value="email" label="Email">
            <Input name="email" onChange={onChange} />
          </Form.Item>
          <Form.Item value="city" label="City">
            <Input name="city" />
          </Form.Item>
          <Form.Item value="country" label="Country">
            <Select
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <Radio name="isActive"  onChange={onChange}/>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
      <Button type="primary" htmlType="submit">
        Submit
      </Button>
    </Form.Item>

        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;








