    //action
    return {
      type: BUY_CAKE,
      info: "first redux action",
    };
  }
  //state
  const initialState = {
    numOfCakes: 10,
  };
  // //reducer function
  const reducer = (state = initialState, action) => {
    switch (action.type) {
      case BUY_CAKE:
