import React, { useEffect, useState } from "react";
import { Card, Input, Select, Button, Form } from "antd";
import { CheckCircleTwoTone } from "@ant-design/icons";
import { reducer, fetchUsers } from "../others/asyncAction";
import store from "../redux/store";
import { AppInput } from "./UserDetail.style";
const UserDetail = () => {
  const [user, setUser] = useState({ firstname: "", email: "", isTrue: false });
  store.subscribe(() => {
    if (store.getState().isTrue) {
      setUser({ isTrue: true });
    }
  });
  const handleSubmit = (event) => {
    // setUser({isTrue : true});
    store.dispatch(fetchUsers());
    localStorage.setItem("user", user.firstname);
  };
  const onChange = (event) => {
    setUser({ ...user, [event.target.name]: event.target.value });
    console.log(user);
  };

  return (
    <div>
      <Card
        hoverable
        borderd={true}
        style={{
          backgroundImage: "linear-gradient(to right,pink,skyblue)",
          marginTop: 100,
          width: 600,
          height: 700,
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: 100,
          marginLeft: 600,
        }}
      >
        <Form onFinish={handleSubmit,on} onFinishFailed={onFinishFailed}>
          <h2>Registration Form</h2>
          <Form.Item label="FirstName"
          rules={[{ required: true, message: 'Please input your Firstname!'}]}>
            <AppInput
              placeholder="FirstName"
              name="firstname"
              onChange={onChange}
            />
          </Form.Item>
          <Form.Item 
           rules={[{ required: true, message: 'Please input your Lastname!'}]}value="lastname" label="LastName">
            <AppInput
              placeholder="LastName"
              name="lastname"
            />
          </Form.Item>
          <Form.Item 
           rules={[{ required: true, message: 'Please input your Email!'}]}
          value="email" label="Email">
            <AppInput
              placeholder="Email"
              name="email"
              onChange={onChange}
            />
          </Form.Item>
          <Form.Item 
           rules={[{ required: true, message: 'Please input your City!'}]}
          value="city" label="City">
            <AppInput  placeholder="City" name="city" />
          </Form.Item>
          <Form.Item 
           rules={[{ required: true, message: 'Please Choose your country !'}]}
          value="country" label="Country">
            <Select
              name="country"
              defaultValue="choose"
              options={[
                {
                  value: "India",
                  label: "India",
                },
                {
                  value: "U.S",
                  label: "U.S",
                },
                {
                  value: "Australia",
                  label: "Australia",
                },
              ]}
            ></Select>
          </Form.Item>
          <Form.Item label="IsActive">
            <CheckCircleTwoTone
              style={{ fontSize: "15px" }}
              twoToneColor={user.isTrue ? "green" : "red"}
            ></CheckCircleTwoTone>
          </Form.Item>
          <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
            <Button type="primary" htmlType="submit">
              Submit
            </Button>
          </Form.Item>
        </Form>
      </Card>
    </div>
  );
};
export default UserDetail;

